import logo from './logo.svg';

function Navbar() {
  const navbarStyle = {
    width: '100%',
    backgroundColor: '#222',
    padding: '15px',
    textAlign: 'center',
  };

  const menuStyle = {
    listStyle: 'none',
    display: 'flex',
    gap: '20px',
    justifyContent: 'center',
    margin: 0,
    padding: 0,
  };

  const linkStyle = {
    color: '#ffffff',
    textDecoration: 'none',
    fontWeight: '400',
    transition: 'color 0.3s ease',
  };

  const linkHoverStyle = { color: '#ffd700' };

  return (
    <nav style={navbarStyle}>
      <ul style={menuStyle}>
        <li>
          <a href="#" style={linkStyle} onMouseOver={(e) => e.target.style.color = linkHoverStyle.color} onMouseOut={(e) => e.target.style.color = linkStyle.color}>
            Accueil
          </a>
        </li>
        <li>
          <a href="#" style={linkStyle} onMouseOver={(e) => e.target.style.color = linkHoverStyle.color} onMouseOut={(e) => e.target.style.color = linkStyle.color}>
            Équipe
          </a>
        </li>
        <li>
          <a href="#" style={linkStyle} onMouseOver={(e) => e.target.style.color = linkHoverStyle.color} onMouseOut={(e) => e.target.style.color = linkStyle.color}>
            Services
          </a>
        </li>
        <li>
          <a href="#" style={linkStyle} onMouseOver={(e) => e.target.style.color = linkHoverStyle.color} onMouseOut={(e) => e.target.style.color = linkStyle.color}>
            Contact
          </a>
        </li>
      </ul>
    </nav>
  );
}

function Header() {
  const headerStyle = { textAlign: 'center', marginBottom: '40px' };
  const titleStyle = { fontSize: '2.8rem', color: '#222', marginBottom: '10px', fontWeight: '700', letterSpacing: '1px' };
  const subtitleStyle = { fontSize: '1.2rem', color: '#555', fontWeight: '300' };

  return (
    <header style={headerStyle}>
      <h1 style={titleStyle}>Découvrez notre équipe</h1>
      <p style={subtitleStyle}>Une présentation élégante et moderne</p>
    </header>
  );
}

function About() {
  const aboutStyle = {
    backgroundColor: '#ffffff',
    borderRadius: '16px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
    padding: '20px',
    textAlign: 'center',
    maxWidth: '800px',
    marginBottom: '40px',
  };

  const titleStyle = { fontSize: '1.8rem', color: '#222', marginBottom: '10px', fontWeight: '600' };
  const paragraphStyle = { fontSize: '1rem', color: '#666', lineHeight: '1.6', fontWeight: '300' };

  return (
    <section style={aboutStyle}>
      <div>
        <h2 style={titleStyle}>À propos de nous</h2>
        <p style={paragraphStyle}>
          Nous sommes une équipe de passionnés, spécialisée dans la création d'interfaces utilisateur élégantes et fonctionnelles.
          Avec une expertise en développement et en design, nous aimons créer des expériences web modernes et interactives.
        </p>
      </div>
    </section>
  );
}

function Button() {
  const buttonStyle = {
    backgroundColor: '#222',
    color: '#ffffff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    fontSize: '1rem',
  };

  const hoverStyle = { backgroundColor: '#ffd700', color: '#222' };

  return (
    <button
      style={buttonStyle}
      onMouseOver={(e) => Object.assign(e.target.style, hoverStyle)}
      onMouseOut={(e) => Object.assign(e.target.style, buttonStyle)}
    >
      En savoir plus
    </button>
  );
}

function Card() {
  const cardContainerStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
    gap: '20px',
    width: '100%',
    maxWidth: '1200px',
    padding: '20px',
  };

  const cardStyle = {
    backgroundColor: '#ffffff',
    borderRadius: '16px',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
    overflow: 'hidden',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  };

  const cardHoverStyle = {
    transform: 'translateY(-10px)',
    boxShadow: '0 20px 30px rgba(0, 0, 0, 0.15)',
  };

  const cardContentStyle = { padding: '20px', textAlign: 'center' };

  const cardImageStyle = { width: '100%', height: '300px', objectFit: 'cover' };

  const titleStyle = { fontSize: '1.6rem', color: '#222', marginBottom: '10px', fontWeight: '600' };

  const descriptionStyle = { fontSize: '1rem', color: '#666', lineHeight: '1.6', fontWeight: '300', marginBottom: '15px' };

  return (
    <div style={cardContainerStyle}>
      <div
        style={cardStyle}
        onMouseOver={(e) => Object.assign(e.currentTarget.style, cardHoverStyle)}
        onMouseOut={(e) => Object.assign(e.currentTarget.style, cardStyle)}
      >
        <img src="https://i.pravatar.cc/300?img=11" alt="Portrait" style={cardImageStyle} />
        <div style={cardContentStyle}>
          <h2 style={titleStyle}>Maxime Faubert</h2>
          <p style={descriptionStyle}>
            Développeur Front-end passionné par les interfaces élégantes et réactives.
          </p>
          <Button />
        </div>
      </div>
    </div>
  );
}

function Footer() {
  const footerStyle = {
    backgroundColor: '#222',
    padding: '15px',
    borderRadius: '12px',
    marginTop: '40px',
    width: '100%',
    textAlign: 'center',
  };

  const linksStyle = {
    listStyle: 'none',
    display: 'flex',
    gap: '20px',
    padding: 0,
    margin: 0,
    justifyContent: 'center',
  };

  const linkStyle = {
    color: '#ffffff',
    textDecoration: 'none',
    fontWeight: '400',
    transition: 'color 0.3s ease',
  };

  const linkHoverStyle = { color: '#ffd700' };

  return (
    <footer style={footerStyle}>
      <ul style={linksStyle}>
        <li>
          <a href="#" style={linkStyle} onMouseOver={(e) => e.target.style.color = linkHoverStyle.color} onMouseOut={(e) => e.target.style.color = linkStyle.color}>
            À propos
          </a>
        </li>
      </ul>
    </footer>
  );
}

function App() {
  return (
    <>
      <Navbar />
      <Header />
      <About />
      <Card />
      <Footer />
    </>
  );
}

export default App;
