export const Card = ({name, photo, description}) => {

    const cardStyle = {
      backgroundColor: "#ffffff",
      borderRadius: "16px",
      boxShadow: "0 10px 20px rgba(0, 0, 0, 0.1)",
      overflow: "hidden",
      transition: "transform 0.3s ease, box-shadow 0.3s ease",
    }
    
    const cardImageStyle = {
      width: "100%",
      height: "300px",
      objectFit: "cover",
    }
    
    const cardContentStyle = {
      padding: "20px",
      textAlign: "center",
    }
    
    const cardTitleStyle = {
      fontSize: "1.6rem",
      color: "#222",
      marginBottom: '10px',
      fontWeight: '600',
    }
    
    const cardDescriptionStyle = {
      fontSize: "1rem",
      color: "#666",
      lineHeight: "1.6",
      fontWeight: "300",
      marginBottom: "15px",
    }

    const cardButton = {
      backgroundColor: "#222",
      color: "#ffffff",
      border: "none",
      padding: "10px 20px",
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      fontSize: '1rem',
    }

    // console.log(props)
  return (
    <div className="card" style={cardStyle}>
        <img src={photo} alt="Portrait" className="card-image" style={cardImageStyle}/>
        <div className="card-content" style={cardContentStyle}>
          <h2 className="card-title" style={cardTitleStyle}>{name}</h2>
          <p className="card-description" style={cardDescriptionStyle}>
              {description}
          </p>
          <Button/>
        </div>
    </div>
  )
}

export default Card
